INSERT INTO meals (id, name, body) VALUES (1, 'Crispy Air Fryer Patatas Bravas', 'When visiting Spain, it is impossible not to try their famous patatas bravas. This delicious combination of crispy potatoes and spicy bravas sauce has been the quintessential dish of Spanish tapas bars for years. Originating in Madrid and further popularized in Barcelona, patatas bravas are the perfect warm introduction to a feast with family and friends, and this easy air fryer take is a simple way to bring the flavours home to you.')